void ValPrint(Js* e, Val* v, bool_t detail, int line) {
    if(!v)
        return;
    if(e->options.quiet)
        return;

    if(line > 0) {
        jprintf(e, 0, "%s%d ", e->colors.cyan, line);
    }
    switch(v->type) {
        case TYPE_FREE:
            jprintf(e,0,"%sfree", e->colors.red);
            break;
        case TYPE_BOOL:
            jprintf(e,0,"%s%s", e->colors.cyan, (v->b) ? "true" : "false");
            break;
        case TYPE_UNDEF:
            jprintf(e,0,"%sundefined", e->colors.cyan);
            break;
        case TYPE_NULL:
            jprintf(e,0,"%snull", e->colors.cyan);
            break;
        case TYPE_NUM: {
                Val* s = ToString(e, v);
                jprintf(e,0,"%s", e->colors.green);
                StrPrint(e, s->str);
                ValDel(e, s);
            }
            break;
        case TYPE_OBJ:
            ObjectPrint(e, v->obj, detail);
            break;
        case TYPE_STR:
            jprintf(e, 0, "%s", e->colors.red);
            StrPrint(e, v->str);
            break;
        case TYPE_SYMA:
        {
            if(!v->descA) jprintf(e,0,"<null>");
            else {
                jprintf(e,0,"%s\'%s\'", e->colors.blue, v->descA);
            }
        }
        break;
        case TYPE_REF:
            jprintf(e,0,"[reference]");
            break;
        case TYPE_COMP:
            ValCompPrint(e, v);
            break;
        case TYPE_LEX:
            jprintf(e,0,"[lex]");
            return;
        case TYPE_ENV:
            jprintf(e,0,"[Env]");
            break;
        case TYPE_DESC:
            jprintf(e,0,"[desc]");
            break; 
        case TYPE_LIST:
            jprintf(e,0,"[List]");
            break;
        case TYPE_PTR:
            jprintf(e,0,"[ptr]");
            break;
        case TYPE_DATA:
            jprintf(e,0,"[data]");
            break;
        case TYPE_BLOCK:
            jprintf(e,0, "[block]");
            break;
        
    }

    jprintf(e,0,"%s", e->colors.normal);
}


