Val* ToLength(Js* e, Val* v) {
    Val* len = ToInteger(e, v);
    RET_IF_ABRUPT_CV1(v, len);
    if(len <= 0) {
        ValDel(e, len);
        return ValNewNum(e, 0);
    }
    number ret = fmin(len->num, pow(2,53)-1);
    ValDel(e, len);
    return ValNewNum(e, ret);
}


