bool_t obj_env_is_unscopable(Js* e, Env* er, Val* v) {
    ObjEnv* oe = (ObjEnv*) er;
    
    if(!oe->unscopables)
        return FALSE;
    
    for(Item* i=oe->unscopables->head; i != NULL; i = i->next) {
        if(ValIsTrueDel(e, SameValue(e, i->value, v))) {
            return TRUE;
        }
    }
    return FALSE;
}

