Val* ObjEnvGetBindingValue(Js* e, Env* er, Val* n, bool_t s) {
    ObjEnv* oe = (ObjEnv*) er;
    Val* value = HasProp(e, oe->bindingObj, n);
    RET_IF_ABRUPT(value);
    if(value->b == FALSE) {
        ValDel(e, value);
        if(s == FALSE)
            return ValNew(e);
        else
            return ThrowRefError(e, 0, n, " not found");
    }
    
    ValDel(e, value);
    return Get(e, oe->bindingObj, n);
}

