Val *EvalTypeOf(Parser* p, Node* n, uint32 flags) {
    Js* e = p->e;
    Val* v = Eval(p, n->right, flags);
    if(v->type == TYPE_REF) {
        if(RefIsUnresolvable(e, v)) {
            ValDel(e, v);
            return ValNewStrA(e, "undefined");
        }
        Val* v2 = GetValue(e, v);
        ValDel(e, v);
        v = v2;
    }
    if(ValIsAbrupt(e, v)) 
        return v;
    
    int t = v->type;
    char * s = NULL;
    switch(t) {
        case TYPE_UNDEF:
            s =  "undefined";
            break;
        case TYPE_NULL:
            s =  "null";
            break;
        case TYPE_BOOL:
            s = "boolean";
            break;
        case TYPE_NUM:
            s = "number";
            break;
        case TYPE_STR:
            s = "string";
            break;
        case TYPE_SYMA:
            s = "symbol";
            break;
        case TYPE_OBJ:
            if(!ObjectGetMethod(e, v->obj, _Call_))
                s = "object";
            else
                s = "function";
            break;
    }
    ValDel(e, v);
    return ValNewStrA(e, s);
}

