#define BITWISE_AND     0
#define BITWISE_OR      1
#define BITWISE_XOR     2
Val* EvalBitwise(Parser* p, Node* n, uint32 flags, int mode) {
    Val* lval = NULL, *rval = NULL, *lnum = NULL, *rnum = NULL, *ret = NULL;
    Js* e = p->e;

    lval = GetVal(p, n->left, flags);
    RET_IF_AB(lval);
    rval = GetVal(p, n->right, flags);
    RET_IF_AB(rval);
    lnum = ToInt32(e, lval);
    RET_IF_AB(lnum);
    rnum = ToInt32(e, rval);
    RET_IF_AB(rnum);

    int32 l = (int32) lnum->num;
    int32 r = (int32) rnum->num;
    int32 x = 0;
    switch(mode) {
        case BITWISE_AND:
            x = l & r;
            break;
        case BITWISE_OR:
            x = l | r;
            break;
        case BITWISE_XOR:
            x = l ^ r;
            break;
    }
    RET(ValNewNum(e, x));

exit:
    IFD(lval);
    IFD(rval);
    IFD(lnum);
    IFD(rnum);
    return ret;    
}

