Val* Math_clz32(Js* e, Val* o, Val* othis, Val* args, Val* newTarget) {
    Val* ret = NULL, *x = GetArg(e, args, 0), *n = ToUint32(e, x);
    RET_IF_AB(n);
    int p = 0;
    uint32 un = (uint32) n->num;
    uint32 mask = 0x80000000;
    while(mask > 0) {
        uint32 res = (un & mask);
        if(res != 0)
            break;
        p++;
        mask = mask >> 1;
    }
    RET(ValNewNum(e, p));

exit:
    IFD(x);
    IFD(n);
    return ret;    
}


