int Platform_LocalCompare(uchar* s1, int l1, uchar* s2, int l2) {
    if(l1 < l2)
        return (l1 -l2);
    if(l1 > l2)
        return (l1 - l2);
    for(int i=0; i < l1; i++) {
        uchar c1 = s1[i];
        uchar c2 = s2[i];
        if(c1 < c2)
            return (c1 -c2);
        if(c1 > c2)
            return (c1 - c2);        
    }
    return 0;
}

