void EnsureContext(Js* e) {
    if(!e->currentContext)
        e->currentContext = ContextNew(e, NULL, NULL, FALSE);
}

void EndContext(Js* e) {
    // To avoid memory test failure, we must _free up the context
    ContextDelete(e, e->currentContext);
    e->currentContext = NULL;
}

