char* DebugFileName(const char* full) {
    char* lastSlash = NULL;
    char* original = (char*) full;
    while(*full) {
        if(!*full) break;
        if(*full == '/')
            lastSlash = (char*) full;
        full++;
    }
    if(!lastSlash)
        return original;
    return lastSlash+1;
}
