    R = E->vtable->getBindingValue(e, E, NX, TRUE);
    ok(t, ValIsThrow(e, R, CurrentRealm(e)->referenceErrorObject), "DeclEnv.getBindingValue(X, strict)");
    ValDel(e, R);
    R = E->vtable->initializeBinding(e, E, NX, VX);
