    ValDel(e, R);
    ValDel(e, O);
    ValDel(e, F);
    ValDel(e, U);

    F = FunctionCreate(e, FUNC_NORMAL, NULL, NULL, NULL, FALSE, NULL,0,0);
    N = ValNewStrA(e, "methodName");
