Val* MockConstructTarget(Js* e, Val* obj, Val* args) {
    Val* ret = ValNewObject(e);
    if(args && args->type == TYPE_LIST && args->list->count > 0) {
        PutA(e, ret, "Y", ListGetValue(e, args->list, 0), TRUE);
    }
    else
        PutA(e, ret, "Y", ValNewNum(e, 3.45), TRUE);
    return ret;
}


